export { ExecOptions } from './DeploymentBuilder';
export { default as resolveInPackage } from './resolveInPackage';
/**
 * Options for build entry points
 */
export interface BuildOptions {
    /**
     * When set to true, the framework build entry point should skip the framework
     * build (e.g. next build, sapper build, nuxt build, etc...)
     */
    skipFramework: boolean;
    /**
     * Excludes the prod entry point from being bundled
     *
     * @default false
     */
    excludeProdEntryPoint?: boolean;
}
