/**
 * Converts a route path pattern to a glob which captures all of the files
 * which it could possibly match
 * @param route The path pattern for a route
 * @returns a glob pattern
 */
export declare function routeToGlob(route: string): string;
/**
 * Converts a route path pattern to a relative directory name
 * that would contain all potential matching files.
 * @param pathPattern The path pattern passed as the first argument to serveStatic()
 * @returns a relative directory path
 */
export declare function rootDirForPath(pathPattern: string): string;
