/**
 * Dynamically requires the version of a package listed in @layer0/core's depedencies
 * This allows us to ensure we are targeting the version of a package, such as globby, that is listed
 * in @layer0/core's package.json, as opposed to whatever version may or may not be included in the user's
 * app, which may not be compatible.
 */
export default function requireInternal(packagePath: string): any;
